import 'dart:convert';

import 'package:base/base.dart';
import 'package:flutter/material.dart';
import 'package:flutter_ume/core/pluggable.dart';

import 'developer_options_page.dart';

class DeveloperOptionsWidget implements Pluggable {
  static final iconBytes = base64Decode(
      '');

  @override
  Widget buildWidget(BuildContext? context) => const SizedBox();

  @override
  String get name => 'DeveloperOptions';

  @override
  String get displayName => '开发者选项';

  @override
  void onTrigger() {
    DeveloperOptionsPage.show(Constant.context);
  }

  @override
  ImageProvider<Object> get iconImageProvider => MemoryImage(iconBytes);
}
